import { useState, useEffect } from "react";
import { DonationButtonHero } from "../Hero/HeroComponents/DonationButtonHero";
import { HeroCarouselObj } from '../Hero/HeroComponents/HeroAssets';


export const Hero = ({ activeSection }) => {

  const [currentIndex, setCurrentIndex] = useState(0);
  const [fadeOut, setFadeOut] = useState(false);

  useEffect(() => {
    const interval = setInterval(() => {
      setFadeOut(true); // Start fading out
      setTimeout(() => {
        setCurrentIndex(prevIndex =>
          prevIndex === HeroCarouselObj.length - 1 ? 0 : prevIndex + 1
        );
        setFadeOut(false); // Reset fade out for next cycle
      }, 500); // The timeout should be less than the interval
    }, 7000); // Changes every 5 seconds

    return () => clearInterval(interval); // Clean up the interval on component unmount
  }, []);

  const currentItem = HeroCarouselObj[currentIndex];

  const handleDotClick = (index) => {
    if (index !== currentIndex) {
      setFadeOut(true); // Start fading out
      setTimeout(() => {
        setCurrentIndex(index);
        setFadeOut(false); // Reset fade out
      }, 500); // This duration should match the transition duration
    }
  };

  return (
    <section className="bg-fixed"
    style={{ 
      backgroundImage: `url(${currentItem.src})`,
      backgroundSize: '100vw 100vh',
      backgroundPosition: 'center 5%',
      transition: 'opacity .5s ease-in-out',
      opacity: fadeOut ? 0 : 1, // Control the opacity here
    }}
    >
      <div className="w-full px-8 py-12 md:py-60 flex flex-col items-center">
        <h1 className="text-center text-white text-4xl md:text-6xl max-w-xl font-semibold">
          {currentItem.title}
        </h1>
        <p className="text-center max-w-xl my-6 text-white">
          {currentItem.caption}
        </p>
        <div className="flex flex-col items-center space-y-6"> {/* Increased vertical spacing */}
          <DonationButtonHero />
        </div>
        {/* Carousel Indicators (Dots) */}
      <div className="mt-6 flex space-x-2">
        {HeroCarouselObj.map((item, index) => (
          <span
            key={item.id}
            className={`block w-3 h-3 bg-white rounded-full cursor-pointer ${currentIndex === index ? 'bg-opacity-100' : 'bg-opacity-50'}`}
            onClick={() => handleDotClick(index)}
          />
        ))}
      </div>
      </div>
    </section>
  );
};
















