import React from "react";
//import "./styles.css";
//import "./styles/tailwind-pre-build.css";
import { loadStripe } from "@stripe/stripe-js";
const stripePromise = loadStripe(
  ""
);
export const DonationButton = ({ itemID, ammount }) => {
  const handleClick = async (event) => {
    const stripe = await stripePromise;
    stripe
      .redirectToCheckout({
        lineItems: [{ price: itemID, quantity: 1 }],
        mode: "payment",
        successUrl: window.location.protocol + "//localpdf.tech/merge",
        cancelUrl: window.location.protocol + "//localpdf.tech/merge",
        submitType: "donate",
      })
      .then(function (result) {
        if (result.error) {
          console.log(result);
        }
      });
  };
  return (
    <button
      className="text-white bg-carolina-700 hover:bg-carolina-800 focus:outline-none focus:ring-4 focus:ring-carolina-300 font-medium rounded-full text-sm px-5 py-2.5 text-center me-2 mb-2 dark:bg-carolina-600 dark:hover:bg-carolina-700 dark:focus:ring-car-800"
      onClick={handleClick}
    >
      Donate now
    </button>
  );
};
