import { motion } from "framer-motion";
import { Link } from 'react-router-dom';
import { useEffect, useState } from "react";
import { FiMenu } from "react-icons/fi";
import ribbonlogo from '../../assets/images/ribbonlogo.jpg';
import facebookIcon from '../../assets/images/svgIcons/facebook.svg';
import instagramIcon from '../../assets/images/svgIcons/instagram.svg';
import twitterIcon from '../../assets/images/svgIcons/twitter.svg';
import '../NavBar/NavBar.css';
import { DonationButton } from "./NarBarComponents/DonationButton";

export const FlipNavWrapper = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [isScrolled, setIsScrolled] = useState(false);

  const handleScroll = () => {
    if (window.scrollY > 50) {
      setIsScrolled(true);
    } else {
      setIsScrolled(false);
    }
  };

  useEffect(() => {
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <div className="bg-transparent">
      <FlipNav isOpen={isOpen} setIsOpen={setIsOpen} isScrolled={isScrolled} />
    </div>
  );
};

const FlipNav = ({ isOpen, setIsOpen, isScrolled }) => {
  return (
    <nav className={`navbar-fixed flex justify-between items-center bg-transparent p-8 w-full ${isScrolled ? 'scrolled-nav' : ''}`}>
      <Link to="/" className="lg:flex hidden">
        <img src={ribbonlogo} style={{ width: '100px', height: 'auto' }} alt="Ribbon Logo" />
      </Link>
      <motion.button
        whileHover={{ scale: 1.05 }}
        whileTap={{ scale: 0.95 }}
        className="block lg:hidden text-gray-950 text-2xl"
        onClick={() => setIsOpen(prev => !prev)}
      >
        <FiMenu />
      </motion.button>
      <NavMenu isOpen={isOpen} />
    </nav>
  );
};

const NavMenu = ({ isOpen }) => {
  return (
    <motion.div
      variants={menuVariants}
      initial="closed"
      animate={isOpen ? "open" : "closed"}
      className="absolute p-4 bg-white shadow-lg left-0 right-0 top-100 origin-top flex flex-col gap-4 lg:hidden"
    >
      <NavLink text="Sponsors" />
      <NavLink text="Community" />
      <NavLink text="Events" />
      <NavLink text="Impact" />
      <NavLink text="Contact" />
      <NavLink text="Subscribe" />
      <SocialMediaIcons />
      <DonationButton />
    </motion.div>
  );
};

const NavLink = ({ text }) => {
  return (
    <a
      href={`#${text.toLowerCase()}`}
      rel="nofollow"
      className="h-[30px] overflow-hidden font-medium"
    >
      <motion.div whileHover={{ y: -30 }}>
        <span className="flex items-center h-[30px] text-purple-500">{text}</span>
        <span className="flex items-center h-[30px] text-purple-600">
          {text}
        </span>
      </motion.div>
    </a>
  );
};

const SocialMediaIcons = () => {
  return (
