import upcomingEvents from './EventCardData';
export const EventsCard = () => {
    return (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
            {upcomingEvents.map(event => (
                <div key={event.id} className="max-w-sm bg-white border border-gray-200 rounded-lg shadow dark:bg-gray-800 dark:border-gray-700">
                    <a href={event.moreInfoUrl}>
                        <img className="rounded-t-lg" src={event.imageUrl} alt={event.title} />
                    </a>
                    <div className="p-5">
                        <a href={event.moreInfoUrl}>
                            <h5 className="mb-2 text-2xl font-bold tracking-tight text-gray-900 dark:text-white">{event.title}</h5>
                        </a>
                        <p className="mb-3 font-normal text-gray-700 dark:text-gray-400">{event.description}</p>
                        <a href={event.moreInfoUrl} className="inline-flex items-center px-3 py-2 text-sm font-medium text-center text-white bg-blue-700 rounded-lg hover:bg-blue-800 focus:ring-4 focus:outline-none focus:ring-blue-300 dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800">
                            Read more
                            {/* SVG icon */}
                        </a>
                    </div>
                </div>
            ))}
        </div>
    );
};
