import { useState, useEffect } from "react";
import { DonationButton } from "../NavBar/NarBarComponents/DonationButton";
import killeenpic1 from '../../assets/images/killeenpic1.jpg'
import killeenpic2 from '../../assets/images/killeenpic2.jpg';

const HeroCarouselObj = [
    {
        id: 1,
        src: killeenpic1,
        title: 'Shock The World',
        caption: "Welcome to the Joe Killeen Foundation",
    },
    {
        id: 2,
        src: killeenpic2,
        title: 'Hello World',
        caption: "akjfbhklsjbckjsdfvjsdfbvlkzjbvlksdfjbvlkjsdf",
    }
];

export const LogoHero = () => {

  const [currentIndex, setCurrentIndex] = useState(0);
  const [fadeOut, setFadeOut] = useState(false);

  useEffect(() => {
    const interval = setInterval(() => {
      setFadeOut(true); // Start fading out
      setTimeout(() => {
        setCurrentIndex(prevIndex =>
          prevIndex === HeroCarouselObj.length - 1 ? 0 : prevIndex + 1
        );
        setFadeOut(false); // Reset fade out for next cycle
      }, 1000); // The timeout should be less than the interval
    }, 7000); // Changes every 5 seconds

    return () => clearInterval(interval); // Clean up the interval on component unmount
  }, []);

  const currentItem = HeroCarouselObj[currentIndex];

  const handleDotClick = (index) => {
    setCurrentIndex(index);
  };

  return (
    <section className="bg-fixed relative"
    style={{ 
      backgroundImage: `url(${currentItem.src})`,
      backgroundSize: 'cover',
      backgroundPosition: 'center',
      transition: 'opacity 1s ease-in-out',
      opacity: fadeOut ? 0 : 1.4, // Control the opacity here
    }}
    >
      <div className="w-full px-8 py-12 md:py-60 flex flex-col items-center">
        <h1 className="text-center text-white text-4xl md:text-6xl max-w-xl font-semibold">
          {currentItem.title}
        </h1>
        <p className="text-center max-w-xl my-6 text-white">
          {currentItem.caption}
        </p>
        {/*<button className="bg-indigo-600 text-white text-base md:text-lg font-medium px-8 py-2 shadow-[3px_3px_0_black] hover:shadow-[1px_1px_0_black] hover:translate-x-[3px] hover:translate-y-[3px] transition-all">
          Donate
        </button>*/}
        <DonationButton />
        {/* Carousel Indicators (Dots) */}
      <div className="left-1/2 transform -translate-x-1/2 flex space-x-2">
        {HeroCarouselObj.map((item, index) => (
          <span
            key={item.id}
            className={`block w-3 h-3 bg-white rounded-full cursor-pointer ${currentIndex === index ? 'bg-opacity-100' : 'bg-opacity-50'}`}
            onClick={() => handleDotClick(index)}
          />
        ))}
      </div>
      </div>
    </section>
  );
};
















