/*import { motion } from "framer-motion";
import { Link } from 'react-router-dom';
import { useEffect, useState } from "react";
import { FiMenu, FiArrowRight } from "react-icons/fi";
import { DonationButton } from "./NarBarComponents/DonationButton";
import ribbonlogo from '../../assets/images/ribbonlogo.jpg';
import facebookIcon from '../../assets/images/svgIcons/facebook.svg';
import instagramIcon from '../../assets/images/svgIcons/instagram.svg';
import twitterIcon from '../../assets/images/svgIcons/twitter.svg';
import '../NavBar/NavBar.css';

export const FlipNavWrapper = () => {
  return (
    <div className="bg-transparent">
      <FlipNav />
    </div>
  );
};

const FlipNav = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [isScrolled, setIsScrolled] = useState(false);

  const handleScroll = () => {
    if (window.scrollY > 50) {
      setIsScrolled(true);
    } else {
      setIsScrolled(false);
    }
  };

  useEffect(() => {
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <nav className="navbar-fixed">
      <nav className={`flex justify-between items-center bg-transparent p-8 w-full ${isScrolled ? 'scrolled-nav' : ''}`}>
        <div className="flex-1 flex justify-start gap-8">
          <Link to="/">
            <img src={ribbonlogo} style={{ width: '100px', height: 'auto' }} alt="Ribbon Logo" />
          </Link>
        </div>      
        <div className="flex flex-col items-center">
          <NavLeft setIsOpen={setIsOpen} />
          <NavMenu isOpen={isOpen} />
        </div>
        <div className="flex-1 flex justify-end gap-8">
          <span className='navbar-icons'>
            <div className='flex gap-2'>
              <a href=''><img src={facebookIcon} alt='Facebook' style={{ width: '30px', height: '50px' }} /></a>
              <a href=''><img src={instagramIcon} alt='Facebook' style={{ width: '30px', height: '50px' }} /></a>
              <a href=''><img src={twitterIcon} alt='Facebook' style={{ width: '30px', height: '50px' }} /></a>
            </div>
          </span>
          <DonationButton />
        </div>
      </nav>
    </nav>
  );
};

const NavLeft = ({ setIsOpen }) => {
  return (
    <div className="flex flex-col lg:flex-row items-center justify-center gap-20">
      <motion.button
        whileHover={{ scale: 1.05 }}
        whileTap={{ scale: 0.95 }}
        className="block lg:hidden text-gray-950 text-2xl"
        onClick={() => setIsOpen((pv) => !pv)}
      >
        <FiMenu />
      </motion.button>
      <NavLink text="Sponsors" />
      <NavLink text="Community" />
      <NavLink text="Events" />
      <NavLink text="Impact" />
      <NavLink text="Contact" />
      <NavLink text="Subscribe" />
    </div>
  );
};

const NavLink = ({ text }) => {
  return (
    <a
      href={text.toLowerCase()}
      rel="nofollow"
      className="hidden lg:block h-[30px] overflow-hidden font-medium"
    >
      <motion.div whileHover={{ y: -30 }}>
        <span className="flex items-center h-[30px] text-purple-500">{text}</span>
        <span className="flex items-center h-[30px] text-purple-600">
          {text}
        </span>
      </motion.div>
    </a>
  );
};

