import { PaymentElement, useStripe, useElements } from "@stripe/react-stripe-js";
import { useState } from "react";
import './Stripe.css';

export default function CheckoutForm() {
  const stripe = useStripe();
  const elements = useElements();

  const [message, setMessage] = useState(null);
  const [isProcessing, setIsProcessing] = useState(false);
  const [amount, setAmount] = useState('');
  const [clientSecret, setClientSecret] = useState(null);

  const handleAmountSubmit = async (e) => {
    e.preventDefault();
    setIsProcessing(true);

    const numericAmount = parseInt(amount, 10);
    if (isNaN(numericAmount) || numericAmount <= 0) {
      setMessage("Please enter a valid donation amount.");
      setIsProcessing(false);
      return;
    }

    try {
      const response = await fetch('http://localhost:5252/create-payment-intent', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ amount: numericAmount * 100 }),
      });

      if (!response.ok) {
        throw new Error('Network response was not ok');
      }

      const responseData = await response.json();
      setClientSecret(responseData.clientSecret);
    } catch (error) {
      setMessage("Failed to process the amount. Please try again.");
    }

    setIsProcessing(false);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!stripe || !elements || !clientSecret) {
      // Stripe.js has not yet loaded, or client secret is not set.
      return;
    }

    setIsProcessing(true);

    try {
        const { error } = await stripe.confirmPayment({
          clientSecret,
          elements,
          confirmParams: {
            return_url: `${window.location.origin}/completion`,
          },
        });

        if (error) {
          setMessage(error.message);
        } else {
          setMessage("Payment successful!");
          // Optionally, redirect the user or update the UI
        }
      } catch (error) {
        setMessage("An error occurred during payment processing.");
      } finally {
        // Reset the clientSecret and amount regardless of the outcome
        setClientSecret(null);
        setAmount('');
        setIsProcessing(false);
      }
  };

  return (
    <form id="payment-form">
      {!clientSecret && (
        <>
          <label htmlFor="donation-amount">Donation Amount (EUR):</label>
          <input
            type="number"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            placeholder="Donation amount in EUR"
          />
          <button onClick={handleAmountSubmit} disabled={isProcessing}>
            Submit Amount
          </button>
        </>
      )}
      {clientSecret && (
        <>
          <PaymentElement id="payment-element" />
          <button onClick={handleSubmit} disabled={isProcessing || !stripe || !elements}>
            <span id="button-text">
              {isProcessing ? "Processing ..." : "Pay now"}
            </span>
          </button>
        </>
      )}
      {message && <div id="payment-message">{message}</div>}
    </form>
  );
}
