import { useState, useEffect } from "react";
import { DonationButtonHero } from "../Hero/HeroComponents/DonationButtonHero";
import { HeroCarouselObj } from '../Hero/HeroComponents/HeroAssets';
import { useLocation } from 'react-router-dom';
import './Hero.css'

export const Hero = () => {

  const location = useLocation();
  const path = location.pathname;

  console.log('Current path:', path); // Debugging line


  let currentItem;

  const pathToIndexMap = {
    '/': 0,
    '/about': 1,  
    '/impact': 2,  
    '/events': 3, 
  };

  const index = pathToIndexMap[path];

  if (index !== undefined) {
    currentItem = HeroCarouselObj[index];
  } else {
    // Default content if path doesn't match
    currentItem = HeroCarouselObj[0]; // You can choose another default or create a new default object
  }

  const isHomePage = currentItem === HeroCarouselObj[0];

  return (
    <section className="hero-section bg-fixed">
    <div className=" backgroundIMG bg-fixed"
      style={{ 
        backgroundImage: `url(${currentItem.src})`,
        backgroundSize: '100% auto',
        backgroundPosition: 'center 5%',
        backgroundRepeat: 'no-repeat',
        height: '100vh',
        width: '100vw'
      }}>

      <div className="w-full py-12 md:py-60 flex flex-col items-center">
        <h1 className="text-center text-white text-4xl md:text-6xl max-w-xl font-semibold">
          {currentItem.title}
        </h1>
        <p className="text-center max-w-xl my-6 text-white">
          {currentItem.caption}
        </p>
        {isHomePage && ( // This line checks if it is the home page
          <div className="flex flex-col items-center space-y-6">
            <DonationButtonHero />
          </div>
        )}
        </div>
        {/* Carousel Indicators (Dots) */}
      {/*<div className="mt-6 flex space-x-2">
        {HeroCarouselObj.map((item, index) => (
          <span
            key={item.id}
            className={`block w-3 h-3 bg-white rounded-full cursor-pointer ${currentIndex === index ? 'bg-opacity-100' : 'bg-opacity-50'}`}
            onClick={() => handleDotClick(index)}
          />
        ))}
      </div>*/}
      </div>
    </section>
  );
};



